#
# Structure for table "xyh_article"
#

DROP TABLE IF EXISTS `xyh_article`;
CREATE TABLE `xyh_article` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `title` varchar(60) NOT NULL DEFAULT '' COMMENT '标题',
  `shorttitle` varchar(30) NOT NULL DEFAULT '' COMMENT '副标题',
  `color` char(10) NOT NULL DEFAULT '' COMMENT '标题颜色',
  `copyfrom` varchar(30) NOT NULL DEFAULT '',
  `author` varchar(30) NOT NULL DEFAULT '',
  `keywords` varchar(50) DEFAULT '' COMMENT '关键字',
  `litpic` varchar(150) NOT NULL DEFAULT '' COMMENT '缩略图',
  `content` text COMMENT '内容',
  `description` varchar(255) NOT NULL DEFAULT '' COMMENT '摘要描述',
  `publishtime` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '发布时间',
  `updatetime` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '更新时间',
  `click` smallint(6) unsigned NOT NULL DEFAULT '0' COMMENT '点击数',
  `cid` int(10) unsigned NOT NULL COMMENT '分类ID',
  `commentflag` tinyint(1) NOT NULL DEFAULT '1' COMMENT '允许评论',
  `flag` tinyint(3) unsigned NOT NULL DEFAULT '0' COMMENT '属性',
  `jumpurl` varchar(200) NOT NULL DEFAULT '',
  `status` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '1回收站',
  `userid` int(10) unsigned NOT NULL DEFAULT '0',
  `aid` int(10) unsigned NOT NULL DEFAULT '0' COMMENT 'admin',
  PRIMARY KEY (`id`),
  KEY `cid` (`cid`)
) ENGINE=MyISAM AUTO_INCREMENT=97 DEFAULT CHARSET=utf8;

#
# Data for table "xyh_article"
#

/*!40000 ALTER TABLE `xyh_article` DISABLE KEYS */;
INSERT INTO `xyh_article` VALUES (17,'西藏民族学院','西藏民族学院...','','','','','./uploads/img1/20151113/56458ad07d876.JPG!300X300.jpg','<p><span style=\"font-size:small;\"><img src=\"./uploads/img1/20151113/56458b7488d85.JPG\" width=\"175\" height=\"132\" border=\"0\" hspace=\"0\" vspace=\"0\" title=\"\" style=\"width: 175px; height: 132px; float: right;\"/> &nbsp;&nbsp;西藏民族学院机房位于图书馆大楼一层，层高3.7米，梁下3.2米。装修完成后机房净高2.6米。机房从功能上划分为3个区域: 即主机房、扩容机房、走廊。主机房面积为135平方米，扩容机房面积为62平方米，走廊36平方米。</span></p>',' &nbsp;&nbsp;西藏民族学院机房位于图书馆大楼一层，层高3.7米，梁下3.2米。装修完成后机房净高2.6米。机房从功能上划分为3个区域: 即主机房、扩容机房、走廊。主机房面积为135平方米，扩容机房面积为62平方米，走廊36平方米',1308908373,1447398311,12,27,1,1,'',0,0,1),(18,'中国延安干部学院中心','中国延安干部学院中心机房...','','','','','./uploads/img1/20151113/56458bee7660a.jpg!300X300.jpg','<p><span style=\"font-size:medium;\"><br/></span></p><p><span style=\"font-size:medium;\"><br/></span></p><p><span style=\"font-size:medium;\">中国延安干部学院中心机房</span></p><p><img src=\"./uploads/img1/20151113/56458c32ea7f5.JPG\" width=\"638\" height=\"480\" border=\"0\" hspace=\"0\" vspace=\"0\" title=\"\" style=\"width: 638px; height: 480px;\"/></p>','中国延安干部学院中心机房',1307517962,1447398483,73,27,1,1,'',0,0,1),(19,'西安高新管委会都市之','西安高新管委会都市之门后备机房建设(二期)...','','','','','./uploads/img1/20151113/56458d35b9878.JPG!300X300.jpg','<p><br/></p><p><img src=\"./uploads/img1/20151113/56458d7a5f61b.JPG\" width=\"646\" height=\"486\" border=\"0\" hspace=\"0\" vspace=\"0\" title=\"\" style=\"width: 646px; height: 486px;\"/></p><p><br/></p><p><span style=\"font-size:medium;\">本项目的工作范围：</span></p><p><span style=\"font-size:medium;\">1、机房系统设计与装修；</span></p><p><span style=\"font-size:medium;\">2、机房电力供应系统；</span></p><p><span style=\"font-size:medium;\">3、精密空调和通风系统；</span></p><p><span style=\"font-size:medium;\">4、消防报警系统；</span></p><p><span style=\"font-size:medium;\">5、设备接地与防雷系统；</span></p><p><span style=\"font-size:medium;\">6、安防及门禁系统；</span></p><p><span style=\"font-size:medium;\">7、综合布线系统；</span></p><p><span style=\"font-size:medium;\">8、机柜和KVM管理系统；</span></p><p><span style=\"font-size:medium;\">9、其他机房所需子系统。</span></p><p><br/></p>','本项目的工作范围：1、机房系统设计与装修；2、机房电力供应系统；3、精密空调和通风系统；4、消防报警系统；5、设备接地与防雷系统；6、安防及门禁系统；7、综合布线系统；8、机柜和KVM管理系统；9、其他机房所需子系统。',1306490909,1447398826,47,28,1,1,'',0,0,1),(20,'西安市劳动保障服务中','西安市劳动保障服务中心大楼中心机房...','','','','','./uploads/img1/20151113/56458de53ac9c.jpg!300X300.jpg','<p><span style=\"font-size:large;\"><img src=\"./uploads/img1/20151113/56458e35c0b77.jpg\" width=\"175\" height=\"117\" border=\"0\" hspace=\"0\" vspace=\"0\" title=\"\" style=\"width: 175px; height: 117px; float: right;\"/>西安市劳动保障服务中心大楼中心机房工程</span></p>','西安市劳动保障服务中心大楼中心机房工程',1306490001,1447399008,69,28,1,1,'',0,0,1),(21,'甘肃社会保险事业管理','甘肃社会保险事业管理中心机房...','','','','','./uploads/img1/20151113/56458e95b952f.jpg!300X300.jpg','<p><br/></p><p><span style=\"font-size:small;\"><strong><span style=\"font-size:medium;\">甘肃社会保险事业管理中心机房</span></strong></span></p><p><span style=\"font-size:small;\"><strong><span style=\"font-size:medium;\"><br/></span></strong></span></p><p><img src=\"./uploads/img1/20151113/56458ed7ee0de.jpg\" width=\"624\" height=\"469\" border=\"0\" hspace=\"0\" vspace=\"0\" title=\"\" style=\"width: 624px; height: 469px;\"/></p>','甘肃社会保险事业管理中心机房',1299489814,1447399160,15,28,1,1,'',0,0,1),(22,'西安市交通信息中心网','西安市交通信息中心网络机房建设...','','','','','./uploads/img1/20151113/56458f6224398.JPG!300X300.jpg','<p><span style=\"font-size:medium;\">&nbsp; &nbsp;西安市交通厅运输管理处中心网络机房位于办公楼三楼，该网络机房为“全省运输管理信息系统”、“全省运输办公自动化系统”、“运政网站”提供安全可靠的硬件基础环境，保证机房信息设备及应用系统、数据的稳定运行。</span></p>','&nbsp; &nbsp;西安市交通厅运输管理处中心网络机房位于办公楼三楼，该网络机房为“全省运输管理信息系统”、“全省运输办公自动化系统”、“运政网站”提供安全可靠的硬件基础环境，保证机房信息设备及应用系统、数据的稳定运行。',1299489369,1447399317,75,28,1,1,'',0,0,1),(23,'代家湾小区1号、2号','代家湾小区1号、2号、3号、6号商务...','','','','','./uploads/img1/20151113/56458fe703eb2.jpg!300X300.jpg','<p><br/></p><p><br/></p><p>&nbsp; &nbsp;信息中心机房做为代家湾小区1号、2号、3号、6号商务楼园区网络及电子政务系统机房，以适应不同功能房间的需要，建造一个视野宽阔、层次丰富、具有现代\r\n气息、能适应未来发展的场地，同时满足用户对形象及风格的要求。根据设计规范，我们设计室内装修采用非燃烧材料或难燃材料，选用气密性好、不起尘易清洁、\r\n不霉变，并在一定温度、湿度作用下变形小的材料。</p><p><br/></p><p><img src=\"./uploads/img1/20151113/5645901a41a16.jpg\" width=\"622\" height=\"468\" border=\"0\" hspace=\"0\" vspace=\"0\" title=\"\" style=\"width: 622px; height: 468px;\"/></p>','&nbsp; &nbsp;信息中心机房做为代家湾小区1号、2号、3号、6号商务楼园区网络及电子政务系统机房，以适应不同功能房间的需要，建造一个视野宽阔、层次丰富、具有现代\r\n气息、能适应未来发展的场地，同时满足用户对形象及风格的要求。根据设',1299488224,1447399476,56,28,1,1,'',0,0,1),(24,'陕西邮政大厦','陕西邮政大厦...','','','','','./uploads/img1/20151113/5645906488718.JPG!300X300.jpg','<p><br/></p><p><span style=\"font-size:medium;\">&nbsp; &nbsp;陕西省邮政信息网新建机房区楼面建筑面积5、6楼约1800平方米左右。新机房建设是\r\n陕西省邮政信息大厦重要建设内容之一，邮政信息大厦位于唐延路与唐兴路交汇处东北角，机房位于大厦的5层和6层，5层层高为3.65m,梁下\r\n3.25m（基建方已将楼板下沉270mm）；6层层高为3.4m，消防管下2.85m。<br/>&nbsp;&nbsp;&nbsp; 5层机房分为金融系统机房、预留机房、介质库、备品备件库及风机房。<br/>&nbsp;&nbsp;&nbsp; 6层分为邮务设备区、办公系统区、网络设备区、电源室、监控中心、接待室及更衣室。5，6楼机房消防管路系统已经施工完毕。&nbsp;</span></p><p><span style=\"font-size:medium;\"><br/></span></p><p><span style=\"font-size:medium;\"><img src=\"./uploads/img1/20151113/564590a39c51b.JPG\"/></span></p>','&nbsp; &nbsp;陕西省邮政信息网新建机房区楼面建筑面积5、6楼约1800平方米左右。新机房建设是\r\n陕西省邮政信息大厦重要建设内容之一，邮政信息大厦位于唐延路与唐兴路交汇处东北角，机房位于大厦的5层和6层，5层层高为3.65m,梁',1299487828,1447399595,63,28,1,1,'',0,0,1),(25,'长安银行咸阳分行机房','长安银行咸阳分行机房建设项目...','','','','','./uploads/img1/20151113/56459120131cb.jpg!300X300.jpg','<p><span style=\"font-size:medium;\"><img src=\"./uploads/img1/20151113/564591b280dbc.jpg\" width=\"175\" height=\"132\" border=\"0\" hspace=\"0\" vspace=\"0\" title=\"\" style=\"width: 175px; height: 132px; float: right;\"/> &nbsp;&nbsp;长安银行股份有限公司咸阳分行机房位于咸阳市渭阳路1号，原名咸阳市商业银行，成立于\r\n2002年，为地方性股份制商业银行。去年8月，按照省委、省政府关于整合地方金融资源，组建省级地方法人金融机构的决策部署，我市精心安排，积极推进，\r\n使原咸阳市商业银行顺利并入长安银行，成为全省五家地市设立长安银行分支机构之一。长安银行咸阳分行主要分三个区：主机房、备品间、值班室，本项目是在原\r\n办公区的基础上改建而成，要达到机房的设计、使用标准，必须对大楼的承重、机房进排水、降噪进行科学分析，采取因地制宜的施工方法进行施工。通过我公司项\r\n目经理与甲方紧密的配合和良好的沟通，使项目得到顺利进展并且一次性通过专家组的验收。</span></p>',' &nbsp;&nbsp;长安银行股份有限公司咸阳分行机房位于咸阳市渭阳路1号，原名咸阳市商业银行，成立于\r\n2002年，为地方性股份制商业银行。去年8月，按照省委、省政府关于整合地方金融资源，组建省级地方法人金融机构的决策部署，我市精心安',1322186415,1447399977,47,29,1,1,'',0,0,1),(26,'中国工商银行宁夏分行','中国工商银行宁夏分行灾备机房...','','','','','./uploads/img1/20151113/56459295842b5.jpg!300X300.jpg','<p><br/></p><p><img src=\"./uploads/img1/20151113/564592f016476.jpg\" width=\"175\" height=\"132\" border=\"0\" hspace=\"0\" vspace=\"0\" title=\"\" style=\"width: 175px; height: 132px; float: right;\"/><br/></p><p><br/></p><p><br/></p><p><br/></p><p><br/></p><p><br/></p><p><br/></p><p><img src=\"./uploads/img1/20151113/56459347f1a13.jpg\" width=\"556\" height=\"418\" border=\"0\" hspace=\"0\" vspace=\"0\" title=\"\" style=\"width: 556px; height: 418px;\"/></p><p><br/></p><p><span style=\"font-size:medium;\">&nbsp; &nbsp;中国工商银行宁夏分行灾备中心机房为旧有机房改造，区域面积60平方米。 \r\n本次工程由建筑装饰系统、电气工程系统、综合布线系统、空调新风系统、消防监控五个子系统组成。分为主机房、机房配电室、UPS电池柜室和值班室四个分\r\n区。按工行总行的要求设计为三类机房，本工程满足了计算机设备对温度、湿度、空气洁净度、供电电源的质量(电压、频率、稳定性等)、接地系统、电磁场和振\r\n动等各项技术要求外，还达到了二类机房照度、流动速度、噪音等人性化工作条件的要求。在电子工艺、美学、采光、气体交换、屏蔽、安防、供配电、防雷、防\r\n漏、温湿度等多项专业测评中达到了甲方预期设想，得到了甲方好评。</span></p>','&nbsp; &nbsp;中国工商银行宁夏分行灾备中心机房为旧有机房改造，区域面积60平方米。 \r\n本次工程由建筑装饰系统、电气工程系统、综合布线系统、空调新风系统、消防监控五个子系统组成。分为主机房、机房配电室、UPS电池柜室和值班室四个',1322185965,1447400303,83,29,1,1,'',0,0,1),(27,'宁夏黄河农村商业银行','宁夏黄河农村商业银行...','','','','','./uploads/img1/20151113/564593992fa07.jpg!300X300.jpg','<p><br/></p><p><br/></p><p><span style=\"font-size:medium;\"><strong>&nbsp; &nbsp; 宁夏黄河农村商业银行中心机房</strong></span>&nbsp;&nbsp;</p>','&nbsp; &nbsp; 宁夏黄河农村商业银行中心机房&nbsp;&nbsp;',1307516124,1447400370,68,29,1,1,'',0,0,1),(28,'建设银行宁夏分行生产','建设银行宁夏分行生产机房...','','','','','./uploads/img1/20151113/564594388035c.jpg!300X300.jpg','<p>&nbsp; &nbsp;&nbsp;<span style=\"font-size:medium;\">&nbsp;&nbsp;&nbsp; 中国建设银行宁夏区分行生产机房位于银川市富宁街1号运行中心大楼，建造于80年代初，是由办公楼改造而来。随着总行SFB项目的推广到位，现有机房已不能满足业务发展需求，且存在较大的风险隐患。为此，我行决定将运行中心在四层的基础上将第三层扩改为生产机房。<br/>&nbsp;&nbsp;&nbsp; 中国建设银行宁夏区分行计算机中心机房位于银川市富宁街1号运行中心大楼四层，框架式结构。机房层高为3.4米，主梁下净高为3.0米。建筑面积约为256M2。</span></p><p><br/></p><p><img src=\"./uploads/img1/20151113/5645947b686aa.jpg\" width=\"638\" height=\"480\" border=\"0\" hspace=\"0\" vspace=\"0\" title=\"\" style=\"