#
# Structure for table "xyh_block"
#

DROP TABLE IF EXISTS `xyh_block`;
CREATE TABLE `xyh_block` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `name` varchar(50) NOT NULL DEFAULT '' COMMENT '名称',
  `remark` varchar(200) NOT NULL DEFAULT '' COMMENT '说明',
  `content` text COMMENT '内容',
  `blocktype` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '类型',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=2 DEFAULT CHARSET=utf8;

#
# Data for table "xyh_block"
#

/*!40000 ALTER TABLE `xyh_block` DISABLE KEYS */;
INSERT INTO `xyh_block` VALUES (1,'Introduction','','<p>网络科技有限公司成立于2007年。是一家集科研、生产、开发、销售为一体的互联网产业高新技术企业。 我公司在全国与多个大型门户网站保持密切技术合作与往来；并且与全国多所高校合作，开发与研究互联网深度产品，取得了不错的研究成果，获得良好的好评</p>',3);
/*!40000 ALTER TABLE `xyh_block` ENABLE KEYS */;

#
# Structure for table "xyh_category"
#

DROP TABLE IF EXISTS `xyh_category`;
CREATE TABLE `xyh_category` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `name` varchar(30) NOT NULL DEFAULT '' COMMENT '栏目分类名称',
  `ename` varchar(200) NOT NULL DEFAULT '' COMMENT '别名',
  `catpic` varchar(150) NOT NULL DEFAULT '',
  `pid` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '上级分类',
  `modelid` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '所属模型',
  `type` tinyint(4) NOT NULL DEFAULT '0' COMMENT '类别',
  `seotitle` varchar(50) NOT NULL DEFAULT '',
  `keywords` varchar(50) DEFAULT '' COMMENT '关键字',
  `description` varchar(255) DEFAULT '' COMMENT '关键字',
  `template_category` varchar(60) NOT NULL DEFAULT '',
  `template_list` varchar(60) NOT NULL DEFAULT '',
  `template_show` varchar(60) NOT NULL DEFAULT '',
  `content` text COMMENT '内容',
  `status` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '显示',
  `sort` smallint(6) NOT NULL DEFAULT '100' COMMENT '排序',
  PRIMARY KEY (`id`),
  KEY `pid` (`pid`)
) ENGINE=MyISAM AUTO_INCREMENT=72 DEFAULT CHARSET=utf8 COMMENT='栏目分类表';

#
# Data for table "xyh_category"
#

/*!40000 ALTER TABLE `xyh_category` DISABLE KEYS */;
INSERT INTO `xyh_category` VALUES (1,'公司简介','about','',0,2,0,'公司简介','','','','List_page.html','Show_article.html','',1,1),(2,'产品介绍','chanpinjieshao','',0,3,0,'产品介绍','','','','List_product.html','Show_product.html',NULL,1,2),(3,'UPS','ups','',2,3,0,'UPS','','','','List_product.html','Show_product.html',NULL,1,1),(4,'机房精密空调','jfjmkt','',2,3,0,'机房精密空调','','','','List_product.html','Show_product.html',NULL,1,2),(5,'业务范围','ywfw','',0,2,0,'业务范围','','','','List_page.html','Show_article.html',NULL,1,3),(6,'整体机房设计与施工','ztjfsjysg','',5,2,0,'整体机房设计与施工','','机房设计宗旨         集成系统的节能化考虑，新技术的应用，以节约日后的运行成本；合理的数据路由结构 设计，以防止可能造成系统互联的阻塞；机构紧','','List_page.html','Show_article.html','<p style=\"font-family: 宋体;padding: 0px;margin-top: 0px;margin-bottom: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-family: SimSun\"><strong>&nbsp; &nbsp;&nbsp;&nbsp;</strong><span style=\"font-family: Arial;color: rgb(255, 0, 0);font-size: 18px\"><strong>机房设计宗旨</strong></span></span></p><p style=\"font-family: 宋体;padding: 0px;margin-top: 0px;margin-bottom: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\">&nbsp;</p><p style=\"font-family: 宋体;padding: 0px;margin-top: 0px;margin-bottom: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-family: SimSun\"><span style=\"font-family: Arial;font-size: 18px\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 集成系统的节能化考虑，新技术的应用，以节约日后的运行成本；</span><span style=\"font-family: Arial;font-size: 18px\">合理的数据路由结构</span></span></p><p style=\"font-family: 宋体;padding: 0px;margin-top: 0px;margin-bottom: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-family: SimSun\"></span>&nbsp;</p><p style=\"font-family: 宋体;padding: 0px;margin-top: 0px;margin-bottom: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-family: SimSun\"><span style=\"font-family: Arial;font-size: 18px\">设</span></span><span style=\"font-family: SimSun\"><span style=\"font-family: Arial;font-size: 18px\">计，以防止可能造成系统互联的阻塞；机构紧密的供配电设计，以减少网络设备的电磁干</span></span></p><p style=\"font-family: 宋体;padding: 0px;margin-top: 0px;margin-bottom: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-family: SimSun\"></span>&nbsp;</p><p style=\"font-family: 宋体;padding: 0px;margin-top: 0px;margin-bottom: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-family: SimSun\"><span style=\"font-family: Arial;font-size: 18px\">扰；地板下的等电位设计，以防止雷击及高压静电对系统的破坏；在充分考虑计算系统、网</span></span></p><p style=\"font-family: 宋体;padding: 0px;margin-top: 0px;margin-bottom: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-family: SimSun\"></span>&nbsp;</p><p style=\"font-family: 宋体;padding: 0px;margin-top: 0px;margin-bottom: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-family: SimSun\"><span style=\"font-family: Arial;font-size: 18px\">络、空调、UPS等设备的安全性、先进性的前提下，达到高雅、大方、简朴的风格。</span></span></p><p style=\"font-family: 宋体;padding: 0px;margin-top: 0px;margin-bottom: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\">&nbsp;</p><p style=\"font-family: 宋体;padding: 0px;margin-top: 0px;margin-bottom: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\">&nbsp;</p><p style=\"font-family: 宋体;padding: 0px;margin-top: 0px;margin-bottom: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<img id=\"imageFck\" border=\"0\" name=\"imageFck\" alt=\"\" src=\"./uploads/img1/20151124/5653d16f79c29.jpg\" width=\"552\" height=\"218\" style=\"border: 0px;padding: 0px\"/></p><p style=\"font-family: 宋体;padding: 0px;margin-top: 0px;margin-bottom: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-family: SimSun\">&nbsp;</span></p><p style=\"font-family: 宋体;padding: 0px;margin-top: 0px;margin-bottom: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><a href=\"http://img0.86good.com:8800/upload/web/2966169/webContent/1306484702510.jpg\" style=\";color: rgb(83, 85, 86)\"></a><span style=\"font-family: SimSun\">&nbsp;</span></p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-family: SimSun\">&nbsp;</span></p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-family: SimSun\"></span></span>&nbsp;</p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-family: SimSun\"><span style=\"font-size: medium\">&nbsp;&nbsp;&nbsp;&nbsp;<img id=\"imageFck\" border=\"0\" name=\"imageFck\" alt=\"\" src=\"./uploads/img1/20151124/5653d19e8a9ee.jpg\" width=\"624\" height=\"373\" style=\"border: 0px;padding: 0px\"/></span></span></span></p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-family: SimSun\"><span style=\"font-size: medium\">&nbsp;&nbsp;&nbsp;</span></span></span></p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-family: SimSun\"></span></span>&nbsp;</p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-family: SimSun\"></span></span>&nbsp;</p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-family: SimSun\"></span></span>&nbsp;</p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-family: SimSun\"><span style=\"font-size: medium\">&nbsp;&nbsp;&nbsp; 目前我公司拥有雄厚的工程技术力量，其中本科生、研究生占技术人员总数的</span></span></span><span style=\"font-size: 12px\"><span style=\"font-family: SimSun\"><span style=\"font-size: medium\">90%</span></span></span><span style=\"font-size: 12px\"><span style=\"font-family: SimSun\"><span style=\"font-size: medium\">以上，直接从</span></span></span></p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-family: SimSun\"></span></span>&nbsp;</p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-family: SimSun\"><span style=\"font-size: medium\">事工程设计的人员为工程技术人员的三分之一。高中级职称达到50%以</span></span></span><span style=\"font-size: 12px\"><span style=\"font-family: SimSun\"><span style=\"font-size: medium\">上，</span></span></span><span style=\"font-size: 12px\"><span style=\"font-family: SimSun\"><span style=\"font-size: medium\">这支队伍以青</span></span></span><span style=\"font-size: 12px\"><span style=\"font-family: SimSun\"><span style=\"font-size: medium\">年人为主，平</span></span></span></p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-family: SimSun\"></span></span>&nbsp;</p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-family: SimSun\"><span style=\"font-size: medium\">均年龄在35岁左右，具有技术知识新、精力旺盛、勇于进取的特点。其综合素质在国内</span></span></span><span style=\"font-size: 12px\"><span style=\"font-family: SimSun\"><span style=\"font-size: medium\">同行业公司中</span></span></span></p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-family: SimSun\"></span></span>&nbsp;</p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-family: SimSun\"><span style=\"font-size: medium\">居领先地位，现已具备专业齐全、搭配合理、科学配套的格局，能够涉足工程总承包方案</span></span></span><span style=\"font-size: 12px\"><span style=\"font-family: SimSun\"><span style=\"font-size: medium\">中在工程设</span></span></span></p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-family: SimSun\"></span></span>&nbsp;</p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-family: SimSun\"><span style=\"font-size: medium\">计方面的各项技术要求。</span></span></span></p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-family: SimSun;font-size: medium\">&nbsp;　　</span></span></p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-size: medium\"><span style=\"font-family: SimSun\">&nbsp;&nbsp;&nbsp; 在技术装备方面，直接用于工程设计的计算机已达三十余台，人机装备率达到100%，用于工程</span></span></span><span style=\"font-size: 12px\"><span style=\"font-size: medium\"><span style=\"font-family: SimSun\">设计</span></span></span></p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-size: medium\"></span></span>&nbsp;</p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-size: medium\"><span style=\"font-family: SimSun\">的技术装备总水平超过其它同行业公司。</span></span></span></p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"></span>&nbsp;</p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-family: SimSun;font-size: medium\"><strong>　　</strong></span></span></p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-size: medium\"><span style=\"font-family: SimSun\">&nbsp;&nbsp;&nbsp; 公司现设有建筑、电气、暖通、弱电等四个专业设计部，各设计部均由具有高级工程师职称的设计</span></span></span></p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-size: medium\"></span></span>&nbsp;</p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-size: medium\"><span style=\"font-family: SimSun\">师负责。由三名具有20年以上设计与施工经验的资深高级工程师组成总工办公室，对设计的整体质量</span></span></span></p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-size: medium\"></span></span>&nbsp;</p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-size: medium\"><span style=\"font-family: SimSun\">进行控制。</span></span></span></p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"></span>&nbsp;</p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-family: SimSun;font-size: medium\"><strong>　　</strong></span></span></p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-size: medium\"><span style=\"font-family: SimSun\">&nbsp;&nbsp;&nbsp; 工程设计严格依照《电子计算机机房设计规范》、《通讯机房静电防护通则》、《防静电工作</span></span></span></p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-size: medium\"></span></span>&nbsp;</p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-size: medium\"><span style=\"font-family: SimSun\">区》、</span></span></span><span style=\"font-size: 12px\"><span style=\"font-size: medium\"><span style=\"font-family: SimSun\">《供配电系统设计规范》、《工业企业照明设计标准》以及其他现行国家相关标准规范。</span></span></span></p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"></span>&nbsp;</p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-family: SimSun;font-size: medium\"><strong>　　</strong></span></span></p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-size: medium\"><span style=\"font-family: SimSun\"><strong>&nbsp;&nbsp;&nbsp;&nbsp;<span style=\"color: rgb(0, 0, 153)\">建筑装修系统，</span></strong>是整个机房的基础，它主要起着功能区划分的作用，不仅包括一般机房装修所需</span></span></span></p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-size: medium\"></span></span>&nbsp;</p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-size: medium\"><span style=\"font-family: SimSun\">要的铺抗静电地板、安装微孔回风吊顶，还包括为放置机架、服务器等设备的预留空间,达到防尘、</span></span></span></p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-size: medium\"></span></span>&nbsp;</p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-size: medium\"><span style=\"font-family: SimSun\">防火、放水、防外部电磁干扰、防静电的效果。</span></span></span></p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-family: SimSun;font-size: medium\"><strong>&nbsp;　</strong></span></span></p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-size: medium\"><span style=\"font-family: SimSun\"><strong>&nbsp;&nbsp;&nbsp;<span style=\"color: rgb(0, 0, 153)\">&nbsp;空调新风系统，</span></strong>是运行环境的保障。由于机房里存放着大量并且密度非常高的各种IT设备，因此</span></span></span></p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-size: medium\"></span></span>&nbsp;</p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-size: medium\"><span style=\"font-family: SimSun\">必然产生热量，这就对空调系统提出了更高的要求。要保证设备的可靠运行，需要机房保持一定的温度</span></span></span></p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-size: medium\"></span></span>&nbsp;</p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-size: medium\"><span style=\"font-family: SimSun\">和湿度。同时机房密闭后仅有空调是不行的，还必须补充新风，形成内部循环,使机房保持正压，防止</span></span></span></p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-size: medium\"></span></span>&nbsp;</p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-size: medium\"><span style=\"font-family: SimSun\">外界浮灰进入机房。通过更换或清洗空气过滤器可控制房里尘埃的数量，使之达到一定的净化要求。</span></span></span></p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-size: medium\"></span></span>&nbsp;&nbsp;</p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-family: SimSun;font-size: medium\"><strong>&nbsp;　　</strong></span></span></p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-size: medium\"><span style=\"font-family: SimSun\"><strong>&nbsp;&nbsp;&nbsp;&nbsp;<span style=\"color: rgb(0, 0, 153)\">消防报警及灭火系统，</span></strong>是整体机房安全运行的盾牌。从对火警的探测系统来看，它通过温感、</span></span></span></p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-size: medium\"></span></span>&nbsp;</p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-size: medium\"><span style=\"font-family: SimSun\">烟感探测器在机房的地板下、设备层、吊灯上分3层进行立体检测、报警防护。对于灭火系统来说，</span></span></span></p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-size: medium\"></span></span>&nbsp;</p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-size: medium\"><span style=\"font-family: SimSun\">采用无毒、无味、无色、无污染的气体对机房进行全淹没式灭火。同时通过准确计算对机房的各区</span></span></span></p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-size: medium\"></span></span>&nbsp;</p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-size: medium\"><span style=\"font-family: SimSun\">域进行分区控制和灭火，最大限度地提高火灾的防范能力和最低限度的减少火灾造成的损失。</span></span></span><span style=\"font-size: 12px\"></span>&nbsp;</p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-family: SimSun;font-size: medium\">&nbsp;　　</span></span></p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-size: medium\"><span style=\"font-family: SimSun\"><strong>&nbsp;&nbsp;&nbsp;&nbsp;<span style=\"color: rgb(0, 0, 153)\">动力配电系统，</span></strong>是整体机房高可用性的心脏。他稳定、持续的运行是保障机房设备安全生产、</span></span></span></p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-size: medium\"></span></span>&nbsp;</p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-size: medium\"><span style=\"font-family: SimSun\">减小故障的主要体系。双母线结构、冗余备份电源（市电、发电机、UPS）、智能化精密配电柜控制</span></span></span></p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-size: medium\"></span></span>&nbsp;</p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-size: medium\"><span style=\"font-family: SimSun\">三者相结合构成一套稳定、安全系数最高的配电系统。同时可保证机房设备不低于30%的可扩展性。</span></span></span></p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-size: medium\"></span></span>&nbsp;</p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-size: medium\"><span style=\"font-family: SimSun\">通过监控系统可随时掌握机房数据设备的用电负荷（功率）、电压、频率、电流、元器件的温度变化、</span></span></span></p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-size: medium\"></span></span>&nbsp;</p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-size: medium\"><span style=\"font-family: SimSun\">负载量的变化从而将设备的供电故障降低到最低。</span></span></span><span style=\"font-size: 12px\"></span>&nbsp;</p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-family: SimSun;font-size: medium\"><strong>&nbsp;　</strong></span></span></p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-size: medium\"><span style=\"font-family: SimSun\"><strong>&nbsp;&nbsp;&nbsp;&nbsp;<span style=\"color: rgb(0, 0, 153)\">弱电控制系统，</span></strong>是整体</span></span></span><span style=\"font-size: 12px\"><span style=\"font-size: medium\"><span style=\"font-family: SimSun\">机房的神经中枢。整体机房控制室具有高度自动化，它要求以最少的维</span></span></span></p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-size: medium\"></span></span>&nbsp;</p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-size: medium\"><span style=\"font-family: SimSun\">护人员，运用最优化的运营维护手段，来实时监控每一个机房中设备所处的物理环境。其中的门禁系</span></span></span></p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-size: medium\"></span></span>&nbsp;</p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-size: medium\"><span style=\"font-family: SimSun\">统、</span></span></span><span style=\"font-size: 12px\"><span style=\"font-size: medium\"><span style=\"font-family: SimSun\">安保系统等，要求对整个机房进行无死角的全方位监控。此外，整体机房集中监控系统，包括了机</span></span></span></p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-size: medium\"></span></span>&nbsp;</p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-size: medium\"><span style=\"font-family: SimSun\">房内</span></span></span><span style=\"font-size: 12px\"><span style=\"font-size: medium\"><span style=\"font-family: SimSun\">各种设备(配电盘、发电机、UPS、空调机组、门禁、消防探头、监视图象等)及环境参数的监测。</span></span></span></p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"></span>&nbsp;</p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-family: SimSun;font-size: medium\">&nbsp;　　</span></span></p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-family: SimSun;font-size: medium\">&nbsp;&nbsp;&nbsp; 东升公司的&quot;整体机房&quot;解决方案，对于机房硬设备的要求和理解要不同于一般机房工程。从东升公</span></span></p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"></span>&nbsp;</p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-family: SimSun;font-size: medium\">司自身特点来看，它是从网络电源专家的角度延伸开来，扩展到机房工程领域。凭借对各个行业用户需</span></span></p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"></span>&nbsp;</p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-family: SimSun;font-size: medium\">求的深刻理解，精湛的咨询顾问和工程设计施工方面的专家队伍，先进的规划运营理念和与国际知名供</span></span></p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"></span>&nbsp;</p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-family: SimSun;font-size: medium\">货商的良好合作关系，向国内的用户推出了整体机房的解决方案。它具有专业化、高可用性、整体性、</span></span></p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"></span>&nbsp;</p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-family: SimSun\"><span style=\"font-size: large\"><span style=\"font-size: medium\">模块化等多种特点：</span></span></span></span></p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-family: SimSun;font-size: medium\"><strong>　　</strong></span></span></p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-size: medium\"><span style=\"font-family: SimSun\"><strong>&nbsp;&nbsp;&nbsp;&nbsp;<span style=\"color: rgb(0, 0, 153)\">专业化：</span></strong>东升 &quot;整体机房&quot; 由具有多年行业用户服务经验的技术部统一协调管理，项目服务人员</span></span></span></p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-size: medium\"></span></span>&nbsp;</p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-size: medium\"><span style=\"font-family: SimSun\">都具有多年的国内机房工程的设计和施工经验，聘请的专家顾问还参与了国家机房设计规范的制定。另</span></span></span></p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-size: medium\"></span></span>&nbsp;</p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-size: medium\"><span style=\"font-family: SimSun\">外东升还得到了国内知名的专业机房公司、以及国家消防工程中心等机房相关服务商的共同支持。从前</span></span></span></p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-size: medium\"></span></span>&nbsp;</p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-size: medium\"><span style=\"font-family: SimSun\">期规划选址、中期设计施工、后期调试开通，直至终身维护服务，东升以专业化的机房工程设计和施工</span></span></span></p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-size: medium\"></span></span>&nbsp;</p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-size: medium\"><span style=\"font-family: SimSun\">方面专家服务，经验丰富的项目管理人员、标准化的流程、规模化的运作，让用户充分感受东升作为大</span></span></span></p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-size: medium\"></span></span>&nbsp;</p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-size: medium\"><span style=\"font-family: SimSun\">型专业公司一贯的高品质和系统配置、整体实施、金牌组合、专家服务的独特优势。使用户的机房无论</span></span></span></p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-size: medium\"></span></span>&nbsp;</p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-size: medium\"><span style=\"font-family: SimSun\">从建筑装饰、电气设备、消防安全等方面都满足关于计算机机房的国家规范标准。</span></span></span></p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"></span>&nbsp;</p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-size: medium\"><span style=\"font-family: SimSun\">　<strong>　</strong></span></span></span></p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-size: medium\"><span style=\"font-family: SimSun\"><strong>&nbsp;&nbsp;&nbsp;&nbsp;<span style=\"color: rgb(0, 0, 153)\">高可用性：</span></strong>高可用性是对这种机房建设的整体性能来说的。在企业信息系统中，服务器、网络连</span></span></span></p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-size: medium\"></span></span>&nbsp;</p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-size: medium\"><span style=\"font-family: SimSun\">接</span></span></span><span style=\"font-size: 12px\"><span style=\"font-size: medium\"><span style=\"font-family: SimSun\">设备的可用性等都相当重要，但是没有可靠的电力保障，机房的高可用性也无从谈起。东升公司给整</span></span></span></p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-size: medium\"></span></span>&nbsp;</p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-size: medium\"><span style=\"font-family: SimSun\">体机房提供的电力系统包括市电的输入、不间断电源的保障、柴油发电机的选配、整个电力系统的综合</span></span></span></p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-size: medium\"></span></span>&nbsp;</p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-size: medium\"><span style=\"font-family: SimSun\">布线等各个方面达到一级供电的可用性保障。东升作为全球端到端&quot;网络不间断&quot;全面电源解决方案领导</span></span></span></p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-size: medium\"></span></span>&nbsp;</p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-size: medium\"><span style=\"font-family: SimSun\">厂商，不仅能提供高可靠的的UPS系列产品，而且与世界优秀品牌合作，建立高可靠的不停电供电系</span></span></span></p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-size: medium\"></span></span>&nbsp;</p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-size: medium\"><span style=\"font-family: SimSun\">统。</span></span></span></p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"></span>&nbsp;</p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-family: SimSun;font-size: medium\"><strong>&nbsp;　　</strong></span></span></p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-size: medium\"><span style=\"font-family: SimSun\"><strong>&nbsp;&nbsp;&nbsp;&nbsp;<span style=\"color: rgb(0, 0, 153)\">整体性：</span></strong>东升 &quot;整体机房&quot; 为客户提供的是一站式&quot;交钥匙&quot;工程，降低了采购成本，可减少客户</span></span></span></p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-size: medium\"></span></span>&nbsp;</p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-size: medium\"><span style=\"font-family: SimSun\">物力和人力的投入，客户无需考虑过多的技术细节问题。有了东升整体机房解决方案，就如同为客户提</span></span></span></p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-size: medium\"></span></span>&nbsp;</p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-size: medium\"><span style=\"font-family: SimSun\">供一个统一的接口，将所有的设备供货商、工程承包商、服务供货商整合在一起，系统化的进行设备配</span></span></span></p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-size: medium\"></span></span>&nbsp;</p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-size: medium\"><span style=\"font-family: SimSun\">置，保证了整个机房的容量和设备间的配套，满足当前和未来发展的需要。此外东升为用户提供永久的</span></span></span></p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-size: medium\"></span></span>&nbsp;</p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-size: medium\"><span style=\"font-family: SimSun\">合同制的服务，保障了用户得到长期的甚至终生的服务维护，充分保证了客户投资的最大利益。</span></span></span></p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"></span>&nbsp;</p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-size: medium\"><span style=\"font-family: SimSun\">&nbsp;<strong>　　</strong></span></span></span></p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-size: medium\"><span style=\"font-family: SimSun\"><strong>&nbsp;&nbsp;&nbsp;<span style=\"color: rgb(0, 0, 153)\">&nbsp;模块化：</span></strong>东升为中小机房用户设计方案时，采用工业化、流水线的运作理念，适时恰当的根据用</span></span></span></p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-size: medium\"></span></span>&nbsp;</p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-size: medium\"><span style=\"font-family: SimSun\">户的不同情况和特点以及需求，进行了更优化的组合。在提供的方案中采用了因地制宜的做法，把建筑</span></span></span></p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-size: medium\"></span></span>&nbsp;</p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-size: medium\"><span style=\"font-family: SimSun\">装修系统、配电、空调、消防全都做成标准化的方案，制定具有针对性的方案设计，提供模块化服务模</span></span></span></p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-size: medium\"></span></span>&nbsp;</p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"><span style=\"font-size: medium\"><span style=\"font-family: SimSun\">式设计。</span></span></span></p><p style=\"font-family: 宋体;padding: 0px;color: rgb(83, 85, 86);font-size: 12px;white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"font-size: 12px\"></span>&nbsp;</p><p style=\"text-align: center;\"><strong style=\"font-family: SimHei; font-size: large; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-family: Arial; font-size: 9pt;\">整体机房是</span><span style=\"font-size: 9pt;\">“</span><span style=\"font-family: Arial; font-size: 9pt;\">交钥匙工程</span><span style=\"font-size: 9pt;\">”</span></strong></p><table style=\"WIDTH: 100%; mso-padding-alt: 0cm 0cm 0cm 0cm; mso-cellspacing: 1.5pt\" class=\"MsoNormalTable\" border=\"0\" cellpadding=\"0\" width=\"551\"><tbody><tr style=\"mso-yfti-irow: 0; mso-yfti-firstrow: yes\" class=\"firstRow\"><td style=\"BORDER-BOTTOM: lightgrey 1pt dotted; BORDER-LEFT: lightgrey 1pt dotted; PADDING-BOTTOM: 0cm; BACKGROUND-COLOR: transparent; PADDING-LEFT: 0cm; PADDING-RIGHT: 0cm; BORDER-TOP: lightgrey 1pt dotted; BORDER-RIGHT: lightgrey 1pt dotted; PADDING-TOP: 0cm; mso-border-alt: dotted lightgrey .75pt\" width=\"46\"><p style=\"text-align:left; MARGIN: 0cm 0cm 0pt; mso-pagination: widow-orphan; mso-margin-top-alt: auto; mso-margin-bottom-alt: auto\" class=\"MsoNormal\"><span style=\"font-family:SimSun;font-size:small;mso-font-kerning: 0pt; mso-hansi-font-family: Arial; mso-bidi-font-family: Arial; mso-ascii-font-family: Arial\"><strong>规划</strong></span><span style=\"FONT-SIZE: 9pt; mso-font-kerning: 0pt\"><o:p></o:p></span></p></td><td style=\"BORDER-BOTTOM: lightgrey 1pt dotted; BORDER-LEFT: lightgrey 1pt dotted; PADDING-BOTTOM: 0cm; BACKGROUND-COLOR: transparent; PADDING-LEFT: 0cm; PADDING-RIGHT: 0cm; BORDER-TOP: lightgrey 1pt dotted; BORDER-RIGHT: lightgrey 1pt dotted; PADDING-TOP: 0cm; mso-border-alt: dotte