#
# Structure for table "xyh_product"
#

DROP TABLE IF EXISTS `xyh_product`;
CREATE TABLE `xyh_product` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `title` varchar(30) NOT NULL DEFAULT '' COMMENT '标题',
  `color` char(10) NOT NULL DEFAULT '' COMMENT '标题颜色',
  `keywords` varchar(50) DEFAULT '' COMMENT '关键字',
  `litpic` varchar(150) NOT NULL DEFAULT '' COMMENT '缩略图',
  `pictureurls` text COMMENT '图片地址',
  `content` text COMMENT '内容',
  `description` varchar(255) NOT NULL DEFAULT '' COMMENT '摘要描述',
  `price` decimal(8,2) NOT NULL DEFAULT '0.00',
  `marketprice` decimal(8,2) NOT NULL DEFAULT '0.00',
  `brand` varchar(50) NOT NULL DEFAULT '' COMMENT '品牌',
  `units` varchar(50) NOT NULL DEFAULT '' COMMENT '单位',
  `specification` varchar(50) NOT NULL DEFAULT '' COMMENT '规格',
  `publishtime` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '发布时间',
  `updatetime` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '更新时间',
  `click` smallint(6) unsigned NOT NULL DEFAULT '0' COMMENT '点击数',
  `cid` int(10) unsigned NOT NULL COMMENT '分类ID',
  `commentflag` tinyint(1) NOT NULL DEFAULT '1' COMMENT '允许评论',
  `flag` tinyint(3) unsigned NOT NULL DEFAULT '0' COMMENT '属性',
  `jumpurl` varchar(200) NOT NULL DEFAULT '',
  `status` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '1回收站',
  `userid` int(10) unsigned NOT NULL DEFAULT '0',
  `aid` int(10) unsigned NOT NULL DEFAULT '0' COMMENT 'admin',
  `pno` varchar(5) DEFAULT NULL COMMENT '产品编号',
  PRIMARY KEY (`id`),
  KEY `cid` (`cid`)
) ENGINE=MyISAM AUTO_INCREMENT=28 DEFAULT CHARSET=utf8;

#
# Data for table "xyh_product"
#

/*!40000 ALTER TABLE `xyh_product` DISABLE KEYS */;
INSERT INTO `xyh_product` VALUES (15,'科华FR-UK三进三','','','./uploads/img1/20151113/564549172f73c.jpg!300X300.jpg','./uploads/img1/20151113/564549172f73c.jpg$$$$$$','<p><span style=\"font-size:small;color:#cc0000;\"><strong>科华FR-UK 30KVA专业型三进三出系列介绍<img src=\"./uploads/img1/20151113/564549172f73c.jpg\" style=\"float: right;\"/></strong></span></p><p>　　科华公司KELONG®专业型三进三出FR-UK系列UPS是科华公司承担的重点国家级火炬计划项目产品，专为数据中心、网络计算机房、智能精\r\n密设备(如测量装置、工业自动化设备等)设计的高性能正弦波不间断电源，系列产品已通过泰尔中心TLC认证、国防通信进网许可证，荣获信息产业部CCID\r\n“质量可靠满意奖”，两次蝉联CCID“性价比满意产品奖”，其高可靠性为金融、通信、保险、交通、税务、军队、证券、能源、教育、政府、制造、企业等行\r\n业提供了重要的电源保护。</p><p>　　科华公司KELONG®专业型三进三出FR-UK系列产品是真正的双变换在线式UPS，采用了先进的IGBT功率器件、性能优越的SPWM逆变器及智能化多模式电池管理技术等国际先进技术，以及丰富的电源管理软件，具有良好的性价比和用户基础。</p><p><br/></p>','科华FR-UK 30KVA专业型三进三出系列介绍　　科华公司KELONG®专业型三进三出FR-UK系列UPS是科华公司承担的重点国家级火炬计划项目产品，专为数据中心、网络计算机房、智能精\r\n密设备(如测量装置、工业自动化设备等)设计的高性能',0.00,0.00,'UPS','','',1309340298,1447901392,24,3,1,5,'',0,0,1,'002'),(16,'科华FR-UK三进单','','','./uploads/img1/20151113/5645499f5f8f4.jpg!300X300.jpg','./uploads/img1/20151113/5645499f5f8f4.jpg$$$$$$','<p>一、科华FR-UK 30KVA专业型三进单出系列介绍<img src=\"./uploads/img1/20151113/5645499f5f8f4.jpg\" style=\"float: right;\"/></p><p>　　科华公司KELONG®专业型三进单出FR-UK系列UPS是科华公司承担的重点国家级火炬计划项目产品，专为数据中心、网络计算机房、智能精\r\n密设备(如测量装置、工业自动化设备等)设计的高性能正弦波不间断电源，系列产品已通过泰尔中心TLC认证、国防通信进网许可证，荣获信息产业部CCID\r\n“质量可靠满意奖”，两次蝉联CCID“性价比满意产品奖”，其高可靠性为金融、通信、保险、交通、税务、军队、证券、能源、教育、政府、制造、企业等行\r\n业提供了重要的电源保护。</p><p>　　科华公司KELONG®专业型三进单出FR-UK系列产品是真正的双变换在线式UPS，采用了先进的IGBT功率器件、性能优越的SPWM逆变器及智能化多模式电池管理技术等国际先进技术，以及丰富的电源管理软件，具有良好的性价比和用户基础。</p><p>　　二、专业型三进单出FR-UK系列UPS主要技术特点</p><p>　　自主专利的MMBM电池管理技术</p><p>　　自主产权的智能化多模式电池管理系统，综合了传统恒压充电、恒流充电、二阶段充电等充电方式的优点，采用微处理器控制技术对电池进行多模式管理，提高UPS的充电效能，延长电池的使用寿命。</p><p>　　完善的电池管理功能：</p><p>　　·无风险双模式电池在线测试功能</p><p>　　·充电电压温度补偿功能</p><p>　　·电池放电终止电压智能调节功能</p><p>　　·电池极性反接保护和告警</p><p>　　·电池定期自动放电激活</p><p>　　·自动设定充电电流功能</p><p>　　·充电功率扩展功能</p><p>　　·重要操作密码锁定</p><p>　　应用特点：</p><p>　　·克服常规充电方式下电池极板腐蚀和水分流失的难题，延长电池使用寿命;</p><p>　　·智能化多模式充电，充电能力强，大大缩短了电池充电时间;</p><p>　　·大部分时间充电器处于休眠状态，提高充电器的可靠性;</p><p>　　·避免因长期浮充引起的电池极板蚀变和水分损耗，延长电池寿命;</p><p>　　·机器内预留充电功能模块、电池充电电流可以多级设置，配置多组电池时无需外置充电器即可现场升级;</p><p>　　·根据用户的具体情况，出厂前设定好参数，使系统处于最佳工作状态。</p><p>　　自主专利MMBM-2智能化电池监控管理系统(选项)</p><p>　　专为UPS蓄电池的日常监测、维护而设计，由电池监测采集单元和集中通信监控器组成，采用专业的数字化控制技术、RS485数据传输和在线监测\r\n管理系统，可在线实时监测、记录UPS蓄电池组中任何一节电池的端电压、充放电电流、电池温度等参数，独有的在线容量分析技术与智能告警功能可以帮助精确\r\n快速查找出电池组中的落后单体，有效降低电池检修和维护工作量，提高系统工作的可靠性和测试的安全性。</p><p>　　应用特点：</p><p>　　·可测量蓄电池组总电压、单体电池电压及回路电流</p><p>　　·全中文LCD显示电压、电流、电池温度等实时监测数据，并可进行系统设置(如电池容量设置、并联组数设置、单组节数设置等)</p><p>　　·对各种电池测试数据进行综合分析，判断电池使用状态</p><p>　　·电池系统异常的声光告警功能</p><p>　　·具有时钟显示和重要监测信息的记录以及掉电保存功能</p><p>　　·具有RS232、RS485接口，及远程干接点通讯功能，能与PC或其他监控设备通讯</p><p>　　·独特校验技术，管理方便、安装简便</p><p>　　自主专利的无主从自适应并联技术</p><p>　　·具有超强的并联能力，无需设定并机数目，可任意在线并联扩容或N+1冗余并联，成倍提高输出功率。</p><p>　　·各UPS并联单元自主独立，无需严格匹配，保证并联工程实现的简易性。</p><p>　　·边建设边投资，具更高的适应性、可用性、可靠性、可扩展性及更低的维护费用</p><p>　　·自适应控制技术保证各UPS并联单元安装、维护简便，运行安全可靠。</p><p>　　灵活的组网监控方案</p><p>　　① 远程监控面板和远程监控器</p><p>　　远程监控面板或远程监控器通过电缆与UPS的RS-485接口连接，距离可达1000米以上，可连续显示UPS工作状态及运行参数，在UPS非正常状态时(如停电或UPS故障等)发出报警。</p><p>　　② 单机近程监控管理</p><p>　　UPS通过RS232接口与计算机串口连接，计算机与UPS建立通信联系，即时监控UPS运行状态，执行UPS自我诊断程序，定时发送查询指令，电源异常时，可弹出告警界面或自动中止各种程序的运行，自动存盘，并即时通报有关信息。</p><p>　　③ 多计算机监控管理</p><p>　　网络中指定的某一台服务器或工作站(称本地机)，通过RS232串口线与UPS通讯，网络上其他服务器或工作站(远端机)与本地机通讯，本地机与远端机都运行UPS电源管理软件，由本地机控制管理一台或多台远端机的报警关机等功能</p><p>　　④ UPS网络化管理</p><p>　　支持SNMP网络管理协议，通过SNMP网络适配器，UPS系统作为一个独立节点接入网络，即可实现网管功能。用户可通过浏览器访问远程的UPS(Web功能)，也可通过网络平台进行远端监控和管理UPS(SNMP功能)。</p><p>　　中文LCD大液晶功能</p><p>　　全中文LCD显示设计，操作简便、方便日常管理和维护，可实时显示UPS的运行参数和运行状态，机内CPU可以记录历史事件和报警信息，信息存\r\n储量高达1000条，包括：停电来电时刻记录，深度放电(达到50%以上)次数，旁路时刻记录，各种告警记录，负数变化记录，保护动作历史记录等。</p><p>　　保护功能设计：</p><p>　　·可设置输出过载保护点</p><p>　　·可设定电池告警保护点</p><p>　　·可设定输入频率同步范围，同步速度及超范围告警点</p><p>　　·可设定市电输入异常(过高/过低)告警点</p><p>　　标配输出隔离变压器</p><p>　　标准配置输出隔离变压器，具有很强的抗负载冲击和短路保护能力，即使逆变器故障击穿时也能保护负载完全不受威胁，还可以选配旁路隔离变压器，实现零地电压值为0V，有效滤除各种杂讯，确保负载设备安全可靠。</p><p>　　冷启动、自启动功能</p><p>　　UPS加装特殊的限流电路，在无市电状态下，可直接用电池组启动UPS，满足应急需求，在电池放电到欠压保护后，市电恢复时可以自动启动UPS，更具有无人值守功能。</p><p>　　手动维护旁路设计</p><p>　　标配设计手动维护旁路通道，保证机器在维修时仍然可以对负载进行不间断供电,提高系统可靠性和可维护性。</p><p>　　全数字化DSP控制</p><p>　　UPS的逆变控制、相位同步、输入整流控制、逻辑控制等全部DSP数字控制，精度高、速度快、整机综合性能好。</p><p>　　可靠的电磁兼容特性</p><p>　　通过权威机构和专业电磁兼容测试，包括传导干扰、辐射干扰、传导抗扰性、辐射抗扰性、电源跌落、群脉冲、静电放电、浪涌等专项内容，优异的电磁兼容特性不仅可以完全滤除各种电网干扰，同时能够有效降低和消除UPS自身产生的干扰适合高频通信、广电声像系统的专业应用。</p><p>　　升级配置(选配件)</p><p>　　·输入隔离变压器</p><p>　　·旁路隔离变压器</p><p>　　·无源PFC</p><p>　　·ECO效率优化器</p><p>　　·EPO紧急关断系统</p><p>　　·输出二次下电二路开关</p><p>　　每个用户的网络特点、电力环境都不相同，电力保护要求也随之变化，通过科华的定制化端到端综合解决方案，专业化方案实施能力和主动式三级服务体系，让您的供电系统更安全、更可靠。</p><p>　　端到端电源解决方案配置：</p><p>　　·配电系统：发电机(市电)、双电源自动切换(ATS)、自动切换配电柜、配电改造</p><p>　　·不间断UPS电源系统：FR-UK(20KVA-50KVA)单机与并机系统方案、蓄电池系统、MMBM-2电池监控管理系统</p><p>　　·监控系统：远程集中监控系统</p><p>　　·防雷系统：按照防护等级，实施防雷工程</p><p>　　·综合布线系统：将空调、电力电源、消防、防雷、监控及照明等系统线路统一设计和施工，便于集中管理和维护。</p><p>　　双回路端到端电源解决方案拓朴图：</p><p>　　三、FR-UK3130T UPS主要技术指标</p><table border=\"1\" cellspacing=\"0\" cellpadding=\"0\"><tbody><tr class=\"firstRow\"><td valign=\"top\" width=\"204\" colspan=\"2\"><p style=\"text-align:center;\">型号</p><p>指标</p></td><td><p style=\"text-align:center;\"><strong>FR-UK3130T</strong></p></td></tr><tr><td rowspan=\"5\"><p style=\"text-align:center;\">输入特性</p></td><td valign=\"top\"><p>电压范围(Vac)</p></td><td valign=\"top\"><p style=\"text-align:center;\">380±25%</p></td></tr><tr><td valign=\"top\"><p>整流输入频率范围(Hz)</p></td><td valign=\"top\"><p style=\"text-align:center;\">40-65Hz</p></td></tr><tr><td valign=\"top\"><p>旁路同步追踪范围(Hz)</p></td><td valign=\"top\"><p style=\"text-align:center;\">50±5%</p></td></tr><tr><td valign=\"top\"><p>相&nbsp;&nbsp;&nbsp; 数</p></td><td valign=\"top\"><p style=\"text-align:center;\">三相五线</p></td></tr><tr><td valign=\"top\"><p>电池电压(VDC)</p></td><td valign=\"top\"><p style=\"text-align:center;\">12V×29= 348V</p></td></tr><tr><td rowspan=\"9\"><p style=\"text-align:center;\">输出特性</p></td><td><p>容量（KVA）</p></td><td><p style=\"text-align:center;\">30</p></td></tr><tr><td valign=\"top\"><p>电压(Vac)</p></td><td valign=\"top\"><p style=\"text-align:center;\">220±1％，隔离变压器输出</p></td></tr><tr><td valign=\"top\"><p>频率(Hz)</p></td><td valign=\"top\"><p style=\"text-align:center;\">市电正常，自动同步跟踪；市电失败，本机50±0.5%</p></td></tr><tr><td valign=\"top\"><p>并联方式（并机型）</p></td><td valign=\"top\"><p style=\"text-align:center;\">无主从自适应并联均流，可多台扩容并联或N+1并联冗余</p></td></tr><tr><td valign=\"top\"><p>波形失真</p></td><td valign=\"top\"><p style=\"text-align:center;\">线性负载下THD≤3%</p></td></tr><tr><td valign=\"top\"><p>静态旁路转换时间（ms）</p></td><td valign=\"top\"><p style=\"text-align:center;\">0</p></td></tr><tr><td valign=\"top\"><p>电池效率</p></td><td valign=\"top\"><p style=\"text-align:center;\">≥90％</p></td></tr><tr><td valign=\"top\"><p>过载能力</p></td><td valign=\"top\"><p style=\"text-align:center;\">125％全载时持续1分钟</p></td></tr><tr><td valign=\"top\"><p>输出方式</p></td><td valign=\"top\"><p style=\"text-align:center;\">接线排</p></td></tr><tr><td rowspan=\"20\"><p style=\"text-align:center;\">其</p><p style=\"text-align:center;\">它</p><p style=\"text-align:center;\">特</p><p style=\"text-align:center;\">性</p></td><td><p>手动维护旁路</p></td><td valign=\"top\"><p style=\"text-align:center;\">提供无转换时间的维修旁路开关</p></td></tr><tr><td valign=\"top\"><p>冷启动功能</p></td><td valign=\"top\"><p style=\"text-align:center;\">具备直流启动功能</p></td></tr><tr><td valign=\"top\"><p>备用时间</p></td><td valign=\"top\"><p style=\"text-align:center;\">电池供电时间可任意配置</p></td></tr><tr><td valign=\"top\"><p>充电特性</p></td><td valign=\"top\"><p style=\"text-align:center;\">采用智能化电池管理系统，独有的多模式充电提高电池的可靠性和使用寿命。</p></td></tr><tr><td valign=\"top\"><p>电池管理</p></td><td valign=\"top\"><p style=\"text-align:center;\">定时自动均/浮充转换，具备电池自测试功能，当电池异常时自动告警并判断电池好坏</p></td></tr><tr><td valign=\"top\"><p>电池监测功能（选件）</p></td><td valign=\"top\"><p style=\"text-align:center;\">MMBM-2电池监控管理系统，实时监测单节电池工作状态</p></td></tr><tr><td valign=\"top\"><p>LCD显示</p></td><td valign=\"top\"><p style=\"text-align:center;\">输入电压、输出电压、电池电压、负载大小、历史记录的查询等</p></td></tr><tr><td valign=\"top\"><p>LED显示</p></td><td valign=\"top\"><p style=\"text-align:center;\">整机的工作状态</p></td></tr><tr><td valign=\"top\"><p>报警功能</p></td><td valign=\"top\"><p style=\"text-align:center;\">市电异常、电池欠压、过载、UPS故障</p></td></tr><tr><td valign=\"top\"><p>通讯功能</p></td><td valign=\"top\"><p style=\"text-align:center;\">提供干接点通讯和RS232/RS485，实现UPS的智能监控。</p></td></tr><tr><td valign=\"top\"><p>保护功能</p></td><td valign=\"top\"><p style=\"text-align:center;\">电池低压保护、过载保护、过温保护、输出短路保护、输出过压保护</p></td></tr><tr><td valign=\"top\"><p>远程监控（选件）</p></td><td><p style=\"text-align:center;\">独立的数字化远程监控器，通过RS485支持，距离可达1000米，实现安全、便捷的远程监控。</p></td></tr><tr><td valign=\"top\"><p>防雷（选件）</p></td><td><p style=\"text-align:center;\">进口三相防雷器符合IEC1312分级保护标准</p></td></tr><tr><td valign=\"top\"><p>电磁兼容</p></td><td valign=\"top\"><p style=\"text-align:center;\">符合GB7260.2-2003</p></td></tr><tr><td valign=\"top\"><p>噪音（dB）</p></td><td valign=\"top\"><p style=\"text-align:center;\">＜65（离箱体正面一米处）</p></td></tr><tr><td valign=\"top\"><p>冷却方式</p></td><td valign=\"top\"><p style=\"text-align:center;\">强制风冷</p></td></tr><tr><td valign=\"top\"><p>工作温度（℃）</p></td><td valign=\"top\"><p style=\"text-align:center;\">0～40</p></td></tr><tr><td valign=\"top\"><p>环境湿度</p></td><td valign=\"top\"><p style=\"text-align:center;\">0～95%，无冷凝</p></td></tr><tr><td valign=\"top\"><p>尺寸(宽×深×高)(mm)</p></td><td><p style=\"text-align:center;\">400×810×1180</p></td></tr><tr><td valign=\"top\"><p>重量(kg)</p></td><td><p style=\"text-align:center;\">300</p></td></tr></tbody></table><p><br/></p>','一、科华FR-UK 30KVA专业型三进单出系列介绍　　科华公司KELONG®专业型三进单出FR-UK系列UPS是科华公司承担的重点国家级火炬计划项目产品，专为数据中心、网络计算机房、智能精\r\n密设备(如测量装置、工业自动化设备等)设计的高',0.00,0.00,'UPS','','',1309340347,1447901411,76,3,1,5,'',0,0,1,'001'),(17,'APC Symmet','','','./uploads/img1/20151113/56454c656c8cb.jpg!300X300.jpg','./uploads/img1/20151113/56454c656c8cb.jpg$$$$$$','<p><span style=\"font-size:small;\">&nbsp; &nbsp; &nbsp;<img src=\"./uploads/img1/20151113/56454c656c8cb.jpg\" style=\"float: right;\"/>APC Symmetra PX \r\n是世界一流的冗余可扩容电源保护系统，它经济有效，可用性高。Symmetra PX \r\n与最新数据中心设计无缝集成，是一个真正的模块化系统。由专用的冗余模块组成 – \r\n电源、智能模块、电池和旁路，全都设计为可方便有效地维修，这种架构可根据需求的增长或可用性级别的提高来扩展电源和运行时间。Symmetra PX \r\n是驱动 APC InfraStruXure® 系统的核心电源系统，用于中小型数据中心，但也可为大型数据中心的单个“区域”供电。便于管理的 \r\nSymmetra PX 具有自诊断功能和标准化的模板，可减少人为错误，从而提高数据中心的总体可靠性。</span></p><p><span style=\"font-size:small;\"><strong>性能与优势&nbsp;</strong></span></p><table class=\"section\"><tbody><tr class=\"firstRow\"><td class=\"main_heading\" colspan=\"2\"><span style=\"font-size:small;color:#000099;\"><strong>可用性 <br/></strong></span></td></tr><tr class=\"line_item line_item_odd\"><td valign=\"top\"><span style=\"font-size:small;\">可配置为 N+1 内部冗余 </span></td><td valign=\"top\"><span style=\"font-size:small;\">允许配置一个或比支持连接负载所必需的更多的电源模块，这样的冗余提供了高可用性</span></td></tr><tr class=\"line_item line_item_even\"><td valign=\"top\"><span style=\"font-size:small;\">冗余智能模块 </span></td><td valign=\"top\"><span style=\"font-size:small;\">给予关键 UPS 功能冗余通讯路径，向 UPS 连接负载提供更高的可用性</span></td></tr><tr class=\"line_item line_item_odd\"><td valign=\"top\"><span style=\"font-size:small;\">电源模块并行连接 </span></td><td valign=\"top\"><span style=\"font-size:small;\">可从隔离模块故障中立即、无缝地恢复，增强可用性</span></td></tr><tr class=\"line_item line_item_even\"><td valign=\"top\"><span style=\"font-size:small;\">并联电池模块 </span></td><td valign=\"top\"><span style=\"font-size:small;\">通过冗余电池提供更高的可用性</span></td></tr><tr class=\"line_item line_item_odd\"><td valign=\"top\"><span style=\"font-size:small;\">热插拔电源模块 </span></td><td valign=\"top\"><span style=\"font-size:small;\">在更换电源模块期间，确保干净、不间断电源以保护设备</span></td></tr><tr class=\"line_item line_item_even\"><td valign=\"top\"><span style=\"font-size:small;\">热插拔智能模块 </span></td><td valign=\"top\"><span style=\"font-size:small;\">在更换智能模块期间，确保干净、不间断电源以保护设备</span></td></tr><tr class=\"line_item line_item_odd\"><td valign=\"top\"><span style=\"font-size:small;\">热插拔电池 </span></td><td valign=\"top\"><span style=\"font-size:small;\">在更换电池的整个过程中，确保干净、不间断电源以保护设备</span></td></tr><tr class=\"line_item line_item_even\"><td valign=\"top\"><span style=\"font-size:small;\">模块化设计 </span></td><td valign=\"top\"><span style=\"font-size:small;\">通过自我诊断，现场可替换模块而提供快速可用性，减少维护需求</span></td></tr><tr class=\"line_item line_item_odd\"><td valign=\"top\"><span style=\"font-size:small;\">网络管理 </span></td><td valign=\"top\"><span style=\"font-size:small;\">提供了通过网络对 UPS 进行远程管理</span></td></tr><tr class=\"line_item line_item_even\"><td valign=\"top\"><span style=\"font-size:small;\">自动内部旁路 </span></td><td valign=\"top\"><span style=\"font-size:small;\">在 UPS 超载情况下或故障时向连接负载提供市电供电</span></td></tr><tr><td class=\"spacer\" colspan=\"2\"><span style=\"font-size:small;\">&nbsp;</span></td></tr><tr><td class=\"main_heading\" colspan=\"2\"><span style=\"font-size:small;color:#000099;\"><strong>其他性能优势 <br/></strong></span></td></tr><tr class=\"line_item line_item_odd\"><td valign=\"top\"><span style=\"font-size:small;\">可升级电源容量 </span></td><td valign=\"top\"><span style=\"font-size:small;\">允许以后快速升级减少今天 UPS 规模过大的成本</span></td></tr><tr class=\"line_item line_item_even\"><td valign=\"top\"><span style=\"font-size:small;\">可扩展的运行时间 </span></td><td valign=\"top\"><span style=\"font-size:small;\">需要时允许快速增加更多的运行时间</span></td></tr><tr class=\"line_item line_item_odd\"><td valign=\"top\"><span style=\"font-size:small;\">前面维护 </span></td><td valign=\"top\"><span style=\"font-size:small;\">简化安装和维护同时对空间需求达到最小</span></td></tr><tr class=\"line_item line_item_even\"><td valign=\"top\"><span style=\"font-size:small;\">可编程频率范围 </span></td><td valign=\"top\"><span style=\"font-size:small;\">保证与不同输入频率的兼容性</span></td></tr><tr class=\"line_item line_item_odd\"><td valign=\"top\"><span style=\"font-size:small;\">SmartSlot 插槽 </span></td><td valign=\"top\"><span style=\"font-size:small;\">利用管理卡定制 UPS 性能</span></td></tr><tr class=\"line_item line_item_even\"><td valign=\"top\"><span style=\"font-size:small;\">不用工具更换电池 </span></td><td valign=\"top\"><span style=\"font-size:small;\">可迅速便捷地更换电池</span></td></tr></tbody></table>','     APC Symmetra PX \r\n是世界一流的冗余可扩容电源保护系统，它经济有效，可用性高。Symmetra PX \r\n与最新数据中心设计无缝集成，是一个真正的模块化系统。由专用的冗余模块组成 – ',0.00,0.00,'UPS','','',1309340425,1447901556,36,3,1,5,'',0,0,1,'003'),(18,'艾默生NXL系列','','','./uploads/img1/20151113/56454da9cf901.jpg!300X300.jpg','./uploads/img1/20151113/56454da9cf901.jpg$$$$$$','<p><span style=\"font-size:small;\"><img src=\"./uploads/img1/20151113/56454da9cf901.jpg\" style=\"float: right;\"/>Hipulse-NXL系列大型UPS为三进三出型(三相输入，三相输出)在线式智能交流不间断电源系统，主要适用于大型IDC机房、银行/证券结算中心、通信网管中心、半导体生产线以及大型自动化生产及其控制系统。</span></p><p><span style=\"font-size:large;\">产品特点：</span></p><p style=\"text-align:left;\"><span style=\"font-size:small;\">在线式双变换设计，完全隔离市电及油机可能存在的各类电网污染及电网故障对负载的影响。</span></p><p style=\"text-align:left;\"><span style=\"font-size:small;\">&nbsp;输出功率因数提升至0.9，更适应大规模采用功率因数校正技术的服务器计算机及网络中心，比传统UPS带载能力增加10%以上；</span></p><p style=\"text-align:left;\"><span style=\"font-size:small;\">采用先进的DSP及全数字控制技术，系统稳定性更高；</span></p><p style=\"text-align:left;\"><span style=\"font-size:small;\">标准内置D级防雷器</span></p><p style=\"text-align:left;\"><span style=\"font-size:small;\">多种输入谐波抑制技术，提高电能利用率；</span></p><p style=\"text-align:left;\"><span style=\"font-size:small;\">零相移输出隔离变压器，减小零地电压和负载谐波电流对逆变器的影响；</span></p><p style=\"text-align:left;\"><span style=\"font-size:small;\">超强输出过载及短路能力，确保系统稳定性和极限状态的系统安全；</span></p><p style=\"text-align:left;\"><span style=\"font-size:small;\">先进的分散式自主并联技术，无需集中旁路柜，可实现8台并联；</span></p><p style=\"text-align:left;\"><span style=\"font-size:small;\">标准内置LBS功能</span></p><p><span style=\"font-size:small;\">超宽输入电压、频率范围，适应恶劣电网环境。</span></p><p><br/></p>','Hipulse-NXL系列大型UPS为三进三出型(三相输入，三相输出)在线式智能交流不间断电源系统，主要适用于大型IDC机房、银行/证券结算中心、通信网管中心、半导体生产线以及大型自动化生产及其控制系统。产品特点：在线式双变换设计，完全隔离',0.00,0.00,'UPS','','',1309340540,1447901637,36,3,1,5,'',0,0,1,'005'),(19,'艾默生iTrust','','','./uploads/img1/20151113/56454e27b18a2.gif!300X300.jpg','./uploads/img1/20151113/56454e27b18a2.gif$$$$$$','<p><span style=\"font-size:small;\"><img src=\"./uploads/img1/20151113/56454e27b18a2.gif\" style=\"float: right;\"/>&nbsp;iTrust \r\nUL33系列UPS为三进三出型(三相输入，三相输出)全数字在线式智能交流不间断电源系统，共有四个