#
# Structure for table "xyh_recruitment"
#

DROP TABLE IF EXISTS `xyh_recruitment`;
CREATE TABLE `xyh_recruitment` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `gangwei` varchar(20) DEFAULT NULL COMMENT '岗位',
  `num` int(5) DEFAULT NULL COMMENT '招聘人数',
  `address` varchar(50) DEFAULT NULL COMMENT '工作地点',
  `gongzi` varchar(50) DEFAULT NULL COMMENT '工资待遇',
  `addtime` date DEFAULT NULL COMMENT '发布日期',
  `timeover` varchar(20) DEFAULT NULL COMMENT '有效期限',
  `information` varchar(50) DEFAULT NULL COMMENT '联系方式',
  `info` varchar(2000) DEFAULT NULL COMMENT '招聘要求',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=7 DEFAULT CHARSET=utf8;

